import React, {useState, useEffect, useRef} from "react";
import './comments.css';
import { Rating } from 'react-simple-star-rating';
import { FaStar } from 'react-icons/fa';
import starEmpty from '../../img/star-empty.png';
import PhotoUser from '../../img/image 95.png';
import ThreeDots from '../../img/three-dots.png';
import ThumbUp from '../../img/thumb-up.png';
import ThumbDown from '../../img/thumb-down.png';
import PhotoProduct from '../../img/Frame 427320002.png';
import ArrowBack from '../../img/arrow back.png';
import photoFrame from '../../img/frame photo.png';
import axios from "axios";


const Comment =()=>
{
 /// ADD COUNTER
   const [counter, setCounter] = useState(0);
  const [counterMinus, setCounterMinus] = useState(0);
   
    const plusCounter= ()=> {
        setCounter  (counter + 1);
    }

    const minusCounter= ()=> {
        setCounter  (counter - 1);
        setCounterMinus(counterMinus + 1);
    }

   // add color to star
   const [rating, setRating]= useState(null);
   const [hover, setHover] = useState(null); 


   // add image
   const inputRef= useRef(null);
   const inputRef1= useRef(null);
   const [image, setImage] = useState();
   const [image1, setImage1] = useState();
 
  const handleImageClick = () => {
    
  inputRef.current.click();

  }
  const handleImageClick1 = () => {
    
    inputRef1.current.click();
  
    }


  const handleImageChange= (e) => {
    const file= e.target.files[0];
      setImage(file);
  }  

  const handleImageChange1= (e) => {
    const file1= e.target.files[0];
      setImage1(file1);
  }
  


  // const outImage = () => {
  //   const formdata = new FormData();
  //   formdata.append('image', image);
  //   axios.post('url', formdata);
  // }

  


  return (
    <div className="container-comment">
      <form className='form-comment'>
    <div className='person-comment'>
  
                   <div className='photo-name-comment'>
                    <div className='photo-user'> <img src={PhotoUser}  alt='photo-user'/></div>
                    <div className='name-user'> <strong> Vivian Weaver</strong></div>
                    <div className="btn-infa">  <img src={ThreeDots} alt='dots'/></div>
                    
                   </div>

                   <div className='star-comment'>
                    <div className="stars">
                    {[...Array(5)].map((star,i) => {
                                const ratingValue= i + 1;
                                  return (
                                    <label className='inputRadio'>
                                        <input type="radio"
                                         name="rating" 
                                         className='inputRadio'                                        
                                                                      
                                         value={ratingValue}
                                         onClick= {() => setRating ( ratingValue )}                                       
                                         
                                         />

                                        <FaStar className='star' 
                                        size="40"                                      
                                        color= {ratingValue <= ( hover || rating ) ? "orange" : "gray"}                                         
                                        onMouseEnter={()=> setHover( ratingValue )}
                                        onMouseLeave={()=> setHover(null)}
                                         />
                                    </label>
                                  
                                )
                            })}
                   </div>
                   
                   <div>
                
                    <div className='short-comment'> Love this laptop, like a MacBook but for Windows </div>
                           
                   
                    
                    </div>

                   
                   </div>

                   <div className='date-comment'> Reviewed in the 
                    <div className='country-comment'>  United States</div>
                    <div className='date-of-comment'>  Junuary 22,2024</div>
                   </div>
                   <div className='comment-properties'> CPU: Intel Core i7 - 1360P   Capacity: 16" 32/1TB   Style: Windows 11 PRO</div>

                   <div  className='text-comment'>

                   <textarea className="input-comment"
                    placeholder="Leave comment">

                    </textarea>
                   </div>

                   <div className='advantages-comment'><strong>Advantages:</strong>
                   <textarea className="input-advanteges"
                    placeholder="Leave advanteges">

                    </textarea>
                   </div>
                   <div className='disadvantages-comment'><strong>Disadvantages:</strong>
                   <textarea className="input-disadvantages"
                    placeholder="Leave disadvantages">

                    </textarea>
                   </div>
                   
                   <div  className='photo-panel' >

                    <form  class >
                   

                    <div  onClick={handleImageClick}  className="photo-add">
                    {image? (<img src={URL.createObjectURL(image)} alt=''  ></img>) : (<img src={photoFrame} alt = ''   />) }
                    <input type='file' name='file' ref={inputRef} onChange={handleImageChange} style= {{display: "none"}}/>
                   
                    </div>
                  
                   <div onClick={handleImageClick1} className="photo-add">
                       {image1? (<img src={URL.createObjectURL(image1)} alt=''  ></img> ): (<img src={photoFrame} alt = ''  />) }
                       <input type='file' name='file' ref={inputRef1} onChange={handleImageChange1} style= {{display: "none"}}/>
                     </div>
                    </form>
  
                   </div>

                   <button  className="post-comment" > POST </button>
                   
              </div>
              </form>
              </div>
  )
}



export default Comment;
